import  { useEffect, useState } from 'react';
import { useParams } from 'react-router'; // Link is removed as per request



function ProductDetailPage() {
  // useParams hook extracts dynamic segments from the URL path
  const { id } = useParams(); // 'id' will be the value from /product/:id

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!id) {
      setError("Product ID is missing from the URL.");
      setLoading(false);
      return;
    }

    setLoading(true);
    setError(null);

/*
    const fetchProductDetailsServer = async () => {
  try {
    const response = await api.get(`/v1/products/${id}`);
    setProduct(response.data);
  } catch (err) {
    console.error("Failed to fetch product details:", err);
    setError("Failed to load product details. Please try again.");
  } finally {
    setLoading(false);
  }
};
*/
    

    // Simulate API call to fetch product details
    const fetchProductDetails = async () => {
      try {
        
         const samsungS25ImageBase64 = 'data:image/png;base64,';
        const mockProduct = {
            id: 's25',
            name: 'Samsung Galaxy S25',
            description: 'Introducing our most powerful processor, customised for Galaxy. With improved real-time ray tracing and Vulkan optimization, dive deep into the action with ultra-smooth gameplay thanks to SnapdragonÂ® 8 Elite Mobile Platform for Galaxy.',
            price: 999.99,
            imageUrl: samsungS25ImageBase64, // Using the Base64 image
        };

        // Simulate network delay
        await new Promise(resolve => setTimeout(resolve, 1000));

         setProduct(mockProduct);

      } catch (err) {
        console.error("Failed to fetch product details:", err);
        setError("Failed to load product details. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetails();
  }, [id]); // Re-run effect if 'id' changes in the URL

  if (loading) {
    return (
      <div className="product-detail-container">
        <p>Loading product details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="product-detail-container error-message">
        <p>{error}</p>
        {/* Removed Link as per request */}
      </div>
    );
  }

  if (!product) {
    return (
      <div className="product-detail-container">
        <p>No product data available.</p>
        {/* Removed Link as per request */}
      </div>
    );
  }

  return (
    <div className="product-detail-container">
      <h1 className="product-name">{product.name}</h1>
      <div className="product-content">
        <img
          src={product.imageUrl}
          alt={product.name}
          className="product-image"         
         />
        <div className="product-info">
          <p className="product-price">
            <strong>Price: </strong> $ {product.price.toFixed(2)}
          </p>
          <p className="product-description">
            {product.description}
          </p>
        
        </div>
      </div>
    </div>
  );
}

export default ProductDetailPage;
